import {Link} from "react-router-dom"
import Nav from "../components/Nav";
// import { useSelector } from "react-redux";
import * as authAction from "../features/authSlice"
import { useDispatch } from "react-redux";

// const userLoggin = () => {
//   const user = useSelector((state) => console.log(state))
// }


function SignIn() {

  const usernameInput = document.querySelector("#username")
const passwordInput = document.querySelector("#password")

  const user = useSelector(selectUser)
  const token = "tokenTestValue"
  const dispatch = useDispatch

  const loginTest = () => {
    console.log("email : ", usernameInput.value)
    console.log("password : ", passwordInput.value)
    user.email = usernameInput.value
    user.password = passwordInput.value
    console.log("user : ", user)
    console.log("token : ", token)
    dispatch(authAction.login(user, token))
  }

  return (
    <>
      <Nav logged="false" />
      <main className="main bg-dark">
        <section className="sign-in-content">
          <i className="fa fa-user-circle sign-in-icon"></i>
          <h1>Sign In</h1>
          <form id="loginForm" onSubmit={SignIn}>
            <div className="input-wrapper">
              <label for="username">Username</label>
              <input type="text" id="username"/>
            </div>
            <div className="input-wrapper">
              <label for="password">Password</label>
              <input type="password" id="password"/>
            </div>
            <div className="input-remember">
              <input type="checkbox" id="remember-me"/>
              <label for="remember-me">Remember me</label>
            </div>
            {/* Placeholder jusqu'a la gestion du Backend */}
            <Link className="sign-in-button" to="/user">Sign In</Link>
            {/* <button className="sign-in-button">Sign In</button> */}
          </form>
        </section>
      </main>
    </>
  );
}

export default SignIn;
