import { Navigate } from "react-router-dom";
import Nav from "../components/Nav";
import Account from "../components/Account";
import { useDispatch, useSelector } from "react-redux";
import { profileRequest, selectToken } from "../features/userSlice";

function User() {
  const dispatch = useDispatch();
  const token = useSelector(selectToken);

  const editBtn = document.querySelector("#editBtn").addEventListener()

  //UPDATE USERNAME
  // if (!token) {
  //   return <Navigate replace to={"/sign-in"} />;
  // } else {
    dispatch(profileRequest(token));
    return (
      <>
        <Nav />
        <main className="main bg-dark">
          <div className="header">
            <h1>
              Welcome back
              <br />
              Name Placeholder
            </h1>
            <button className="edit-button" id="editBtn">Edit Name</button>
            <div className="edit-form hidden">
            <h2>Edit user info</h2>
              <form>
                <div className="input-wrapper">
                  <label htmlFor="username">User name : </label>
                  <input type="text" id="username" placeholder={""}/>
                </div>
                <div className="input-wrapper">
                  <label htmlFor="firstname">First name : </label>
                  <input type="text" id="firstname" disabled placeholder={""}/>
                </div>
                <div className="input-wrapper">
                  <label htmlFor="lastname">Last name : </label>
                  <input type="text" id="lastname" disabled placeholder={""}/>
                </div>
                <div className="edit-submit">
                <input
                  className="save-button edit-button"
                  type="submit"
                  value="Save"
                ></input>
                <input
                  className="cancel-button edit-button"
                  type="button"
                  value="Cancel"
                ></input>
                </div>
              </form>
            </div>
          </div>
          <h2 className="sr-only">Accounts</h2>
          <Account
            title="Argent Bank Checking (x8349)"
            amount="$2,082.79"
            description="Available Balance"
          />
          <Account
            title="Argent Bank Savings (x6712)"
            amount="$10,928.42"
            description="Available Balance"
          />
          <Account
            title="Argent Bank Credit Card (x8349)"
            amount="$184.30"
            description="Current Balance"
          />
        </main>
      </>
    );
  }
// }

export default User;
