import Nav from "../components/Nav";
import Account from "../components/Account";
import { useDispatch, useSelector } from "react-redux";
import { selectProfile } from "../features/profileSlice";

function User() {

  const dispatch = useDispatch();

  const getUserIngos = (e) => {
    const usernameInput = document.querySelector("#username");
    const passwordInput = document.querySelector("#password");
    e.preventDefault();
    const token = "tokenTestValue";
    const user = {
      username: usernameInput.value,
      password: passwordInput.value,
    };
    setTimeout(() => {
      dispatch(authAction.login({ user, token }));
      console.log(authAction.login({ user, token }));
    }, 1000);
  };


  const userInfos = useSelector(selectProfile)
  return (
    <>
      <Nav/>
      <main className="main bg-dark">
        <div className="header">
          <h1>
            Welcome back
            <br />
            {userInfos.lastName + ' ' + userInfos.firstName}
          </h1>
          <button className="edit-button">Edit Name</button>
        </div>
        <h2 className="sr-only">Accounts</h2>
        <Account
          title="Argent Bank Checking (x8349)"
          amount="$2,082.79"
          description="Available Balance"
        />
        <Account
          title="Argent Bank Savings (x6712)"
          amount="$10,928.42"
          description="Available Balance"
        />
        <Account
          title="Argent Bank Credit Card (x8349)"
          amount="$184.30"
          description="Current Balance"
        />
      </main>
    </>
  );
}

export default User;
