import { createAsyncThunk, createSlice } from "@reduxjs/toolkit"

const url = "http://localhost:3001/api/v1/"

//Async Logic

export const loginRequest = createAsyncThunk(
    'user/loginRequest',
    async () => {
        console.log("Tentative de Connexion")
        const usernameInput = document.querySelector("#username");
        const passwordInput = document.querySelector("#password");       
        const postMethod = {
            method: "POST",
            headers: {
              "accept" : "application/json",
              "Content-type" : "application/json; charset=UTF-8",
            },
            body: JSON.stringify({
              email: usernameInput.value,
              password: passwordInput.value,
            }),
          }
        const accessToken = await fetch(url + "user/login", postMethod)
        .then((r) => r.json())
        .then((json) => json.body.token)
        return accessToken
    }
)
export const profileRequest = createAsyncThunk(
    'user/profileRequest',
    async (token) => {
        console.log("Récuperation du Profil") 
        const postMethod = {
            method: "POST",
            headers: {
                accept: "application/json",
                Authorization: `Bearer ${token}`
              }
          }
        const userProfile = await fetch(url + "user/profile", postMethod)
        .then((r) => r.json())
        .then((json) => json.body)
        return userProfile
    }
)
export const usernameUpdateRequest = createAsyncThunk(
    'user/usernameUpdateRequest',
    async (props) => {
        console.log("Mise à Jour du Pseudo vers : ", props.updatedUsername)
        const putMethod = {
            method: "PUT",
            headers: {
              "accept" : "application/json",
              "Content-type" : "application/json; charset=UTF-8",
              Authorization: `Bearer ${props.token}`
            },
            body: JSON.stringify({
                userName: props.updatedUsername
            }),
          }
        const updatedProfile = await fetch(url + "user/profile", putMethod)
        .then((r) => r.json())
        .then((json) => json.body)
        return updatedProfile
    }
)


const userSlice = createSlice({
    name: "user",
    initialState: {
        profile: null,
        token: null
    },
    reducers: {
        logout: (state) => {
            state.profile = null
            state.token = null
        }
    },
    extraReducers: (builder) => {
        builder
        .addCase(loginRequest.fulfilled, (state, action) => {
            state.token = action.payload
        })
        .addCase(profileRequest.fulfilled, (state, action) => {
            state.profile = action.payload
        })
        .addCase(usernameUpdateRequest.fulfilled, (state, action) => {
            state.profile = action.payload
        })
    }
})

export default userSlice.reducer

// export const selectUser = (state) => state.auth.user
export const selectToken = (state) => state.user.token
export const selectProfile = (state) => state.user.profile
