import { Navigate } from "react-router-dom";
import Nav from "../components/Nav";
import Account from "../components/Account";
import { useDispatch, useSelector } from "react-redux";
import {
  profileRequest,
  selectToken,
  selectProfile,
  usernameUpdateRequest,
} from "../features/userSlice";
import { useEffect } from "react";

function User() {
  const dispatch = useDispatch();

  const token = useSelector(selectToken);
  const profile = useSelector(selectProfile);

  useEffect(()=> {
    dispatch(profileRequest(token))
  }, [token])

  const toggleForm = (editForm) => {
    editForm.className === "edit-form"
      ? (editForm.className = "edit-form hidden")
      : (editForm.className = "edit-form");
  };
  //UPDATE USERNAME
  if (!token) {
    return <Navigate replace to={"/sign-in"} />;
  } else {
    if (profile) {
      return (
        <>
          <Nav />
          <main className="main bg-dark">
            <div className="header">
              <h1>
                Welcome back
                <br />
                {profile.firstName + " " + profile.lastName}
              </h1>
              <button
                className="edit-button"
                onClick={() => {
                  const editForm = document.getElementById("editForm");
                  toggleForm(editForm);
                }}
              >
                Edit Name
              </button>
              <div className="edit-form hidden" id="editForm">
                <h2>Edit user info</h2>
                <form>
                  <div className="input-wrapper">
                    <label htmlFor="username">User name : </label>
                    <input type="text" id="username" placeholder={profile.userName} />
                  </div>
                  <div className="input-wrapper">
                    <label htmlFor="firstname">First name : </label>
                    <input
                      type="text"
                      id="firstname"
                      disabled
                      placeholder={profile.firstName}
                    />
                  </div>
                  <div className="input-wrapper">
                    <label htmlFor="lastname">Last name : </label>
                    <input
                      type="text"
                      id="lastname"
                      disabled
                      placeholder={profile.lastName}
                    />
                  </div>
                  <div className="edit-submit">
                    <input
                      className="save-button edit-button"
                      type="submit"
                      value="Save"
                      onSubmit={() => (dispatch(usernameUpdateRequest(token, )))}
                    ></input>
                    <input
                      className="cancel-button edit-button"
                      type="button"
                      value="Cancel"
                      onClick={() => {
                        const editForm = document.getElementById("editForm");
                        toggleForm(editForm);
                      }}
                    ></input>
                  </div>
                </form>
              </div>
            </div>
            <h2 className="sr-only">Accounts</h2>
            <Account
              title="Argent Bank Checking (x8349)"
              amount="$2,082.79"
              description="Available Balance"
            />
            <Account
              title="Argent Bank Savings (x6712)"
              amount="$10,928.42"
              description="Available Balance"
            />
            <Account
              title="Argent Bank Credit Card (x8349)"
              amount="$184.30"
              description="Current Balance"
            />
          </main>
        </>
      );
    }
  }
}

export default User;
