import { Navigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { selectToken, loginRequest } from "../features/userSlice";

function SignIn() {
  const dispatch = useDispatch();
  const token = useSelector(selectToken);

  const loginTest = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target)
    const formInfos = {username: formData.get("username"), password: formData.get("password")}
    dispatch(loginRequest(formInfos));
  };

  if (token) {
    return <Navigate replace to={"/user"} />;
  } else {
    return (
      <>
        <main className="main bg-dark">
          <section className="sign-in-content">
            <i className="fa fa-user-circle sign-in-icon"></i>
            <h1>Sign In</h1>
            <form onSubmit={(e) => loginTest(e)}>
              <div className="input-wrapper">
                <label htmlFor="username">Username</label>
                <input type="text" id="username" />
              </div>
              <div className="input-wrapper">
                <label htmlFor="password">Password</label>
                <input type="password" id="password" />
              </div>
              <div className="input-remember">
                <input type="checkbox" id="remember-me" />
                <label htmlFor="remember-me">Remember me</label>
              </div>
              <input
                className="sign-in-button"
                type="submit"
                value="Sign In"
              ></input>
            </form>
          </section>
        </main>
      </>
    );
  }
}
export default SignIn;
