import {Link} from "react-router-dom"
import Nav from "../components/Nav";
import * as authAction from "../features/authSlice"
import { useDispatch, useSelector } from "react-redux";
export const url = "localhost:3001/"

function SignIn() {

  const user = useSelector((state) => state.auth.user)
  const token = useSelector((state) => state.auth.token)
  const dispatch = useDispatch()

  const usernameInput = document.querySelector("#username")
  const passwordInput = document.querySelector("#password")
  const loginForm = document.querySelector("#loginForm")


  const loginTest = (e) => {
    e.preventDefault()
    user = {
      username : usernameInput,
      password : passwordInput,
    }
    token = "tokenTestValue"

  dispatch(authAction.login(user, token))
  }

  loginForm.addEventListener('submit', loginTest())
  return (
    <>
      <Nav logged="false" />
      <main className="main bg-dark">
        <section className="sign-in-content">
          <i className="fa fa-user-circle sign-in-icon"></i>
          <h1>Sign In</h1>
          <form id="loginForm">
            <div className="input-wrapper">
              <label htmlFor="username">Username</label>
              <input type="text" id="username"/>
            </div>
            <div className="input-wrapper">
              <label htmlFor="password">Password</label>
              <input type="password" id="password"/>
            </div>
            <div className="input-remember">
              <input type="checkbox" id="remember-me"/>
              <label htmlFor="remember-me">Remember me</label>
            </div>
            {/* Placeholder jusqu'a la gestion du Backend */}
            {/* <Link className="sign-in-button" to="/user">Sign In</Link> */}
            {/* <button className="sign-in-button">Sign In</button> */}
            <input type="submit" value="Sign In"></input>
          </form>
        </section>
      </main>
    </>
  );
}

export default SignIn;
