import { Link } from "react-router-dom";
import Nav from "../components/Nav";
import * as authAction from "../features/authSlice";
import { useDispatch } from "react-redux";
export const url = "localhost:3001/";
import { useSelector } from "react-redux";
import { selectToken } from "../features/authSlice";

function SignIn() {
  const dispatch = useDispatch();

  const loginTest = (e) => {
    const usernameInput = document.querySelector("#username");
    const passwordInput = document.querySelector("#password");
    e.preventDefault();
    const token = "tokenTestValue";
    const user = {
      username : usernameInput.value,
      password : passwordInput.value,
    };
    setTimeout(() => {
      dispatch(authAction.login({ user, token }));
      console.log(authAction.login({ user, token }));
    }, 1000);

  };

  token = useSelector(selectToken)

  return (

    token === 

    <>
      <Nav/>
      <main className="main bg-dark">
        <section className="sign-in-content">
          <i className="fa fa-user-circle sign-in-icon"></i>
          <h1>Sign In</h1>
          <form onSubmit={(e) => loginTest(e)}>
            <div className="input-wrapper">
              <label htmlFor="username">Username</label>
              <input type="text" id="username" />
            </div>
            <div className="input-wrapper">
              <label htmlFor="password">Password</label>
              <input type="password" id="password" />
            </div>
            <div className="input-remember">
              <input type="checkbox" id="remember-me" />
              <label htmlFor="remember-me">Remember me</label>
            </div>
            {/* Placeholder jusqu'a la gestion du Backend */}
            {/* <Link className="sign-in-button" to="/user">Sign In</Link> */}
            {/* <button className="sign-in-button">Sign In</button> */}
            <input
              className="sign-in-button"
              type="submit"
              value="Sign In"
            ></input>
          </form>
        </section>
      </main>
    </>
  );
}

export default SignIn;
